#!/usr/bin/env python
 
import rospy
#import the code generated by catkin.
#we need ServiceExample for the first message type,
#and the ServiceExampleResponse from the second message type from the  ServiceExample.srv file
from dreamwalker_control.srv import Service_GUI_Command, Service_GUI_CommandResponse


def turn_on_off(request):
 if request.command=="FORWARD":
    return  Service_GUI_CommandResponse('Robot going forward')
 elif request.command=="BACKWARD":
 	return  Service_GUI_CommandResponse('Robot going backward')
 elif request.command=="RIGHT":
 	return  Service_GUI_CommandResponse('Robot going right')
 elif request.command=="LEFT":
 	return  Service_GUI_CommandResponse('Robot going left')
 elif request.command=="SPIN_RIGHT":
 	return  Service_GUI_CommandResponse('Robot turning right')
 elif request.command=="SPIN_LEFT":
 	return  Service_GUI_CommandResponse('Robot turning left')
 elif request.command=="STOP":
    return  Service_GUI_CommandResponse('Robot has been halted')
 
 
rospy.init_node('service_response')
 
service=rospy.Service('command_service',Service_GUI_Command, turn_on_off)
 
rospy.spin()